mapper.select:
select pinboardno, username, pinboardname, create_time, content, likecnt, pictureurl from (SELECT (@row_number:=@row_number+1) as row_number, pinboardno, username, pinboardname, create_time, content, likecnt, pictureurl FROM tbl_pinboard, (SELECT @row_number:=0) as t
WHERE username = #{username}
WHERE pictureurl not like '/ustylenone.jpg'
ORDER BY create_time DESC ) X where X.row_number between #{first} and #{last}

mapper.delete:
delete from
tbl_pinboardlike 
where pinboardno = #{pinboardno} and username = #{username}

mapper.update:
update
tbl_pinboard set pinboardname = #{pinboardname}, content = #{content}
where pinboardno = #{pinboardno}

update
tbl_pinboard set pictureurl = #{pictureurl}
where pinboardno = #{pinboardno}
