mapper.select:
SELECT productid, productname, description, brand, category, subcategory, pictureurl FROM (SELECT (@row_number:=@row_number+1) as row_number, productid, productname, description, brand, category, subcategory, pictureurl FROM tbl_product, (SELECT @row_number:=0) as t
WHERE ${searchOption} LIKE '%${searchKeyword}%'
ORDER BY productid DESC ) X WHERE X.row_number BETWEEN #{first} and #{last}
