mapper.select:
SELECT productid, productname, description, brand, category, subcategory, pictureurl, mainpictureurl FROM (SELECT (@row_number:=@row_number+1) as row_number, productid, productname, description, brand, category, subcategory, pictureurl, mainpictureurl FROM tbl_product, (SELECT @row_number:=0) as t
WHERE ${searchOption} LIKE '%${searchKeyword}%'
ORDER BY productid DESC ) X WHERE X.row_number BETWEEN #{first} and #{last}

choose.when:
update tbl_product
set productname = #{productname}, description = #{description}, brand = #{brand}, 
	category = #{category}, subcategory = #{subcategory}, pictureurl = #{pictureurl}, mainpictureurl = #{mainpictureurl}
where productid = #{productid}
