mapper.select:
select bno, username, title, content, category, regdate, viewcnt, family, parent, depth, indent from (select (@row_number:=@row_number+1) as row_number, bno, username, title, content, category, regdate, viewcnt, family, parent, depth, indent from tbl_qna, (SELECT @row_number:=0) as t
where ${searchOption} like '%${searchKeyword}%'
order by family desc ) X where X.row_number between #{first} and #{last}

select bno, username, title, content, category, regdate, viewcnt, family, parent, depth, indent from (select (@row_number:=@row_number+1) as row_number, bno, username, title, content, category, regdate, viewcnt, family, parent, depth, indent from (select * from tbl_qna where family in (select bno from tbl_qna where username = #{username}) ), (SELECT @row_number:=0) as t
where ${searchOption} like '%${searchKeyword}%'
order by restep desc, bno asc ) X where X.row_number between #{first} and #{last}
