mapper.update:
update tbl_user set password = #{password}, postcode = #{postcode}, phone = #{phone}, realname = #{realname},
addr1 = #{addr1}, addr2 = #{addr2}
where username = #{username}

mapper.select:
select username, email, realname, create_time, addr1, addr2, postcode, phone, point, auth from (SELECT (@row_number:=@row_number+1) as row_number, username, email, realname, create_time, addr1, addr2, postcode, phone, point, auth from tbl_user, (SELECT @row_number:=0) as t
where ${searchOption} like '%${searchKeyword}%'
ORDER BY create_time DESC ) X where X.row_number between #{first} and #{last}
