mapper.select:
SELECT productid, productname, description, brand, category, subcategory, pictureurl, mainpictureurl, originalprice, saleprice FROM (SELECT (@row_number:=@row_number+1) as row_number, productid, productname, description, brand, category, subcategory, pictureurl, mainpictureurl, originalprice, saleprice FROM tbl_product, (SELECT @row_number:=0) as t
WHERE ${searchOption} LIKE '%${searchKeyword}%'
ORDER BY productid DESC ) X WHERE X.row_number BETWEEN #{first} AND #{last}

 		SELECT count(*) 
 		FROM 
 			tbl_product
 		WHERE
			subcategory = #{subcategory}

		AND brand = #{brand}

SELECT productid, productname, description, brand, category, subcategory, pictureurl, mainpictureurl, originalprice, saleprice FROM (SELECT (@row_number:=@row_number+1) as row_number, productid, productname, description, brand, category, subcategory, pictureurl, mainpictureurl, originalprice, saleprice FROM tbl_product, (SELECT @row_number:=0) as t WHERE subcategory = #{subcategory}
AND brand = #{brand}
ORDER BY productname ASC
ORDER BY create_time DESC
ORDER BY saleprice ASC
ORDER BY saleprice DESC
) X WHERE X.row_number BETWEEN #{first} and #{last}

choose.when:
update tbl_product
set productname = #{productname}, description = #{description}, brand = #{brand}, 
	category = #{category}, subcategory = #{subcategory}, pictureurl = #{pictureurl}, 
	mainpictureurl = #{mainpictureurl}, originalprice = #{originalprice}, saleprice = #{saleprice}
where productid = #{productid}

choose.otherwise:
update tbl_product
set productname = #{productname}, description = #{description}, brand = #{brand}, 
	category = #{category}, subcategory = #{subcategory}, pictureurl = '[/ustylenone.jpg]',
	mainpictureurl = '/ustylenone.jpg', originalprice = #{originalprice}, saleprice = #{saleprice}
where productid = #{productid}

mapper.select:

    	select count(I.productid) > 0 as existsales
		from tbl_item I, tbl_sales S
		where I.itemid = S.itemid and productid = #{productid}


    select count(productid) > 0 as existpin
	from tbl_pin
	where productid = #{productid}
